
$(document).ready(function(){
    $("#menu").css("width",$(".main").length*200);
    $(".sub").slideUp(0) ;
    for(i = 0;i < $(".main").length; i++){
        $(".main:eq("+i+")").on("click",{id:i},function(e){
            n = e.data.id;
            $(".sub:eq("+n+")").slideToggle(500);
            $(".sub:not(:eq("+n+"))").slideUp(500);
            })       
    }
})


